B = (0,0);
C = (1,0);
A = extension(B, B + dir(90 - angleA/2), C, C + dir(90 + angleA/2));
D = (A + reflect(B,C)*(A))/2;
E = (B + reflect(A,C)*(B))/2;
F = (C + reflect(A,B)*(C))/2;
