draw((-4,0)--(6,0));
draw((0,-4)--(0,4));
draw(O--A,Arrow(6));
draw(O--R,Arrow(6));
draw(A--R,dashed,Arrow(6));
draw(O--B,Arrow(6));
draw(O--S,Arrow(6));
