E = (B + reflect(C,A)*(B))/2;
F = (C + reflect(A,B)*(C))/2;

draw(A--B--C--cycle);
draw(A--D,dashed);
draw(B--E,dashed);
draw(C--F,dashed);

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
label("$D$", D, S);
label("$E$", E, NE);
